// Mark incoming companies as new
const companies = $('Execute Workflow Trigger').all();
return companies.map(item => ({ json: { ...item.json, _isNewCompany: true } }));

const allItems = $input.all();
const newCompanies = [];
const existingRecords = [];

for (const item of allItems) {
  if (item.json._isNewCompany) {
    newCompanies.push(item);
  } else if (item.json.fields && item.json.fields['Company Name']) {
    existingRecords.push(item);
  } else if (item.json.id && item.json['Company Name']) {
    existingRecords.push({ json: { id: item.json.id, fields: item.json } });
  }
}

const normalize = (str) => str ? String(str).toLowerCase().replace(/[^a-z0-9]/g, '').trim() : '';

// Map existing records: name -> { id, hasUrl }
const existingMap = new Map();
for (const item of existingRecords) {
  const fields = item.json.fields || item.json;
  const name = fields['Company Name'] || '';
  const url = fields['Company URL'] || '';
  const recordId = item.json.id;
  if (name) {
    existingMap.set(normalize(name), { id: recordId, hasUrl: !!url && url.length > 0 });
  }
}

const uniqueCompanies = [];
const addedNames = new Set();

for (const company of newCompanies) {
  const name = company.json.company_name || company.json['Company Name'];
  const newUrl = company.json.company_url || company.json['Company URL'] || '';
  if (!name) continue;

  const normalizedName = normalize(name);
  if (!normalizedName || addedNames.has(normalizedName)) continue;

  const existing = existingMap.get(normalizedName);

  if (existing) {
    // Record exists - check if we should update (has new URL, existing has no URL)
    if (!existing.hasUrl && newUrl) {
      addedNames.add(normalizedName);
      const cleanCompany = { ...company.json };
      delete cleanCompany._isNewCompany;
      cleanCompany._updateRecordId = existing.id; // Mark for update
      uniqueCompanies.push({ json: cleanCompany });
    }
    // Otherwise skip - already exists with URL
    continue;
  }

  // New company - create
  addedNames.add(normalizedName);
  const cleanCompany = { ...company.json };
  delete cleanCompany._isNewCompany;
  uniqueCompanies.push({ json: cleanCompany });
}

if (uniqueCompanies.length === 0) {
  return [{ json: { _empty: true, _message: 'No new companies found' } }];
}

return uniqueCompanies;

const items = $input.all();
return items.map(item => {
  const company = item.json;
  const name = company.company_name || company['Company Name'] || '';
  const searchQuery = '"' + name + '" company funding employees series startup';
  return { json: { ...company, searchQuery } };
});

const braveResponses = $input.all();
const companyDataItems = $('Build Search Query').all();

const peFirms = [
  'Vista Equity', 'Thoma Bravo', 'KKR', 'Blackstone', 'TPG', 'Carlyle',
  'Bain Capital', 'Silver Lake', 'Francisco Partners', 'Accel-KKR',
  'LLR Partners', 'Warburg Pincus', 'Apollo', 'Insight Partners', 'GTCR',
  'Clearlake', 'Welsh Carson', 'Providence Equity', 'Leonard Green',
  'Hellman & Friedman', 'Advent', 'Golden Gate Capital', 'TA Associates'
];

const currentYear = new Date().getFullYear();

return braveResponses.map((item, index) => {
  const braveResponse = item.json;
  const companyData = companyDataItems[index]?.json || {};
  const searchResults = braveResponse?.web?.results || [];
  const allText = searchResults.map(r => `${r.title || ''} ${r.description || ''}`).join(' ');

  // Employee count
  let employeeCount = null;
  const empMatch = allText.match(/([\d,]+)\s*(?:to\s*[\d,]+)?\s*employees?/i);
  if (empMatch) employeeCount = parseInt(empMatch[1].replace(/,/g, ''));

  // Funding stage
  let fundingStage = null;
  const stagePatterns = {
    'Series D': /Series\s*D/i,
    'Series C': /Series\s*C/i,
    'Series B': /Series\s*B/i,
    'Series A': /Series\s*A/i,
    'Seed': /\bSeed\b/i
  };
  for (const [stage, pattern] of Object.entries(stagePatterns)) {
    if (pattern.test(allText)) { fundingStage = stage; break; }
  }

  // Total funding
  let totalFunding = null, totalFundingRaw = null;
  const fundMatch = allText.match(/\$([\d.]+)\s*(million|billion|M|B)/i);
  if (fundMatch) {
    const amount = parseFloat(fundMatch[1]);
    const unit = fundMatch[2].toLowerCase();
    totalFunding = (unit === 'billion' || unit === 'b') ? amount * 1000000000 : amount * 1000000;
    totalFundingRaw = `$${amount}${unit.charAt(0).toUpperCase()}`;
  }

  // PE detection
  const isPEBacked = peFirms.some(f => new RegExp(f, 'i').test(allText));
  const peBackerFound = peFirms.find(f => new RegExp(f, 'i').test(allText)) || null;

  // Founded year
  let foundedYear = null;
  const foundMatch = allText.match(/(?:founded|established|since)\s*(?:in)?\s*(\d{4})/i);
  if (foundMatch) foundedYear = parseInt(foundMatch[1]);

  // Auto-disqualifiers
  const autoDisqualifiers = [];
  if (isPEBacked) autoDisqualifiers.push(`PE-backed (${peBackerFound})`);
  if (employeeCount >= 500) autoDisqualifiers.push(`500+ employees (${employeeCount})`);
  if (totalFunding >= 200000000) autoDisqualifiers.push(`$200M+ funding (${totalFundingRaw})`);
  if (foundedYear && foundedYear < 2017) autoDisqualifiers.push(`Founded ${foundedYear}`);
  if (['Series D'].includes(fundingStage)) autoDisqualifiers.push(`Late stage (${fundingStage})`);

  return {
    json: {
      company_name: companyData.company_name || companyData['Company Name'],
      company_url: companyData.company_url || companyData['Company URL'],
      description: companyData.description || companyData['Company Description'],
      source: companyData.source || companyData['VC Firm'] || 'Unknown',
      funding_stage: fundingStage,
      total_funding: totalFundingRaw,
      employee_count: employeeCount,
      pe_backed: isPEBacked,
      founded_year: foundedYear,
      investors: peBackerFound || '',
      autoDisqualifiers,
      isAutoDisqualified: autoDisqualifiers.length > 0
    }
  };
});

const responses = $input.all();
const companyItems = $('Build Evaluation Prompt').all();

return responses.map((item, index) => {
  const response = item.json;
  const companyData = companyItems[index]?.json || {};

  let evaluation = {};
  try {
    const content = response.content?.[0]?.text || '';
    const jsonMatch = content.match(/\{[\s\S]*\}/);
    if (jsonMatch) evaluation = JSON.parse(jsonMatch[0]);
  } catch (e) {
    evaluation = { bucket: 'WATCH', total_score: 0, summary: 'Parse error - manual review needed' };
  }

  const statusMap = { 'APPLY': 'Apply', 'WATCH': 'Monitor', 'PASS': 'Skip' };
  const nextStepsMap = { 'APPLY': 'Apply Now', 'WATCH': 'Watch for Jobs', 'PASS': 'Skip' };

  return {
    json: {
      _updateRecordId: companyData._updateRecordId || null,
      company_name: companyData.company_name,
      company_url: companyData.company_url,
      description: companyData.description,
      source: companyData.source,
      status: statusMap[evaluation.bucket] || 'Research',
      next_steps: nextStepsMap[evaluation.bucket] || 'Research More',
      total_score: evaluation.total_score || 0,
      sector_match: evaluation.sector_match || 'unknown',
      cs_hire_likelihood: evaluation.cs_hire_likelihood || '',
      product_type: evaluation.product_type || '',
      summary: evaluation.summary || '',
      funding_stage: companyData.funding_stage,
      total_funding: companyData.total_funding,
      employee_count: companyData.employee_count,
      pe_backed: companyData.pe_backed,
      founded_year: companyData.founded_year
    }
  };
});
