const results = [];
const seen = new Set();

const vcData = [
  {
    vc: 'Congruent Ventures',
    companies: [
      ['Arcadia', 'https://www.arcadia.com'],
      ['Aurora Solar', 'https://www.aurorasolar.com'],
      ['BlocPower', 'https://www.blocpower.io'],
      ['Carbon Direct', 'https://www.carbon-direct.com'],
      ['Charm Industrial', 'https://www.charmindustrial.com'],
      ['Common Energy', 'https://www.commonenergy.us'],
      ['Dandelion Energy', 'https://www.dandelionenergy.com'],
      ['Enervee', 'https://www.enervee.com'],
      ['Form Energy', 'https://www.formenergy.com'],
      ['Gradient', 'https://www.gradientcomfort.com'],
      ['Span', 'https://www.span.io'],
      ['WattBuy', 'https://www.wattbuy.com']
    ]
  },
  {
    vc: 'Prelude Ventures',
    companies: [
      ['Impossible Foods', 'https://www.impossiblefoods.com'],
      ['Redwood Materials', 'https://www.redwoodmaterials.com'],
      ['Pivot Bio', 'https://www.pivotbio.com'],
      ['Lilac Solutions', 'https://www.lilacsolutions.com'],
      ['Twelve', 'https://www.twelve.co'],
      ['Boston Metal', 'https://www.bostonmetal.com'],
      ['CarbonCure', 'https://www.carboncure.com'],
      ['Pachama', 'https://www.pachama.com'],
      ['Turntide Technologies', 'https://www.turntide.com']
    ]
  },
  {
    vc: 'Lowercarbon Capital',
    companies: [
      ['Watershed', 'https://www.watershed.com'],
      ['Crusoe', 'https://www.crusoe.ai'],
      ['Electric Hydrogen', 'https://www.eh2.com'],
      ['Fervo Energy', 'https://www.fervoenergy.com'],
      ['Sublime Systems', 'https://www.sublime-systems.com'],
      ['ZeroAvia', 'https://www.zeroavia.com'],
      ['Commonwealth Fusion', 'https://www.cfs.energy'],
      ['Koloma', 'https://www.koloma.com']
    ]
  }
];

for (const { vc, companies } of vcData) {
  for (const [name, url] of companies) {
    const key = name.toLowerCase().replace(/[^a-z0-9]/g, '');
    if (seen.has(key)) continue;
    seen.add(key);
    results.push({
      json: {
        company_name: name,
        company_url: url,
        description: `${name} - ${vc} portfolio company (climate tech)`,
        source: vc
      }
    });
  }
}

return results;
