// Parse Unusual Ventures
const items = $input.all();
const results = [];
const vcName = 'Unusual Ventures';
const seen = new Set();
const today = new Date().toISOString().split('T')[0];

for (const item of items) {
  let html = item.json.data || (typeof item.json === 'string' ? item.json : JSON.stringify(item.json));
  const h4Pattern = /<h4[^>]*>([^<]+)<\/h4>/gi;
  let match;
  while ((match = h4Pattern.exec(html)) !== null) {
    const text = match[1].trim();
    if (text.length < 10 || text.toLowerCase().includes('visit website') || text.toLowerCase().includes('portfolio')) continue;
    const parts = text.split(/\s+is\s+/i);
    if (parts.length < 2) continue;
    const name = parts[0].trim();
    const key = name.toLowerCase();
    if (name.length >= 2 && name.length <= 50 && !seen.has(key)) {
      seen.add(key);
      const context = html.substring(Math.max(0, match.index - 1000), Math.min(html.length, match.index + 1000));
      let url = '';
      const extMatch = context.match(/href=["'](https?:\/\/(?!(?:www\.)?(?:unusual\.vc|linkedin|twitter|facebook|x)\.com)[^"']+)["']/i);
      if (extMatch) url = extMatch[1];
      results.push({ json: { company_name: name, company_url: url, description: text, source: vcName } });
    }
  }
}
return results.length ? results : [{ json: { _empty: true } }];

// Parse First Round Capital
const items = $input.all();
const results = [];
const vcName = 'First Round Capital';
const seen = new Set();

for (const item of items) {
  const companies = item.json.result || [];
  for (const company of companies) {
    const name = company.title || '';
    if (!name || name.length < 2) continue;
    const key = name.toLowerCase().replace(/[^a-z0-9]/g, '');
    if (seen.has(key)) continue;
    seen.add(key);
    const website = company.website || '';
    let description = '';
    if (company.statement && Array.isArray(company.statement)) {
      for (const block of company.statement) {
        if (block.children) for (const child of block.children) if (child.text) description += child.text + ' ';
      }
      description = description.trim();
    }
    if (!description) description = name + ' - First Round Capital portfolio company';
    results.push({ json: { company_name: name, company_url: website, description: description, source: vcName } });
  }
}
return results.length ? results : [{ json: { _empty: true } }];

