// Parse Costanoa Ventures
const items = $input.all();
const results = [];
const vcName = 'Costanoa Ventures';
const seen = new Set();
const headlineWords = ['connects', 'announces', 'launches', 'raises', 'acquires', 'joins', 'partners', 'expands', 'introduces', 'unveils', 'releases', 'data', 'digital', 'offline', 'online', 'purchasing', 'marketing', 'advertising', 'investment', 'funding', 'series', 'million', 'billion'];

for (const item of items) {
  let xml = item.json.data || (typeof item.json === 'string' ? item.json : JSON.stringify(item.json));
  const urlBlockPattern = /<url>([\s\S]*?)<\/url>/gi;
  let match;
  while ((match = urlBlockPattern.exec(xml)) !== null) {
    const block = match[1];
    const locMatch = block.match(/<loc>https?:\/\/costanoa\.vc\/portfolio\/([^<\/]+)<\/loc>/i);
    if (!locMatch) continue;
    const slug = locMatch[1].trim();
    if (!slug || slug.length < 2 || slug === 'portfolio') continue;
    const wordCount = slug.split('-').length;
    if (wordCount > 4) continue;
    const slugLower = slug.toLowerCase();
    if (headlineWords.some(w => slugLower.includes(w))) continue;
    if (slug.length > 40) continue;
    const name = slug.split('-').map(w => w.charAt(0).toUpperCase() + w.slice(1)).join(' ');
    const key = name.toLowerCase().replace(/[^a-z0-9]/g, '');
    if (seen.has(key)) continue;
    seen.add(key);
    results.push({ json: { company_name: name, company_url: 'https://costanoa.vc/portfolio/' + slug, description: name + ' - Costanoa Ventures portfolio company', source: vcName } });
  }
}
return results.length ? results : [{ json: { _empty: true } }];

