// Parse Notable Capital
const items = $input.all();
const results = [];
const vcName = 'Notable Capital';
const seen = new Set();
const skipDomains = ['notablecap', 'linkedin', 'twitter', 'facebook', 'instagram', 'youtube', 'medium', 'google', 'apple', 'amazonaws', 'cloudfront', 'cdn', 'fonts', 'gstatic'];

for (const item of items) {
  let html = item.json.data || (typeof item.json === 'string' ? item.json : JSON.stringify(item.json));
  const linkPattern = /<a[^>]*href=["'](https?:\/\/[^"']+)["'][^>]*>/gi;
  let match;
  while ((match = linkPattern.exec(html)) !== null) {
    const url = match[1].trim();
    if (url.includes('#') || url.length < 10) continue;
    const domainMatch = url.match(/https?:\/\/(?:www\.)?([^.\/]+)/i);
    if (!domainMatch) continue;
    const domain = domainMatch[1].toLowerCase();
    if (skipDomains.some(skip => domain.includes(skip))) continue;
    if (domain.length < 2 || domain.length > 30) continue;
    let name = domain.charAt(0).toUpperCase() + domain.slice(1);
    name = name.replace(/ai$/i, 'AI').replace(/^ai/i, 'AI');
    const key = domain.replace(/[^a-z0-9]/g, '');
    if (key.length < 2 || seen.has(key)) continue;
    seen.add(key);
    results.push({ json: { company_name: name, company_url: url, description: name + ' - Notable Capital portfolio company', source: vcName } });
  }
}
return results.length ? results : [{ json: { _empty: true } }];

// Parse Headline
const items = $input.all();
const results = [];
const vcName = 'Headline';
const seen = new Set();
const junkPatterns = ['privacy', 'statement', 'notice', 'blog', 'latest', 'how we work', 'site notice', 'investing in', 'founder', 'spotlight', 'landscape', 'lookahead', 'why is', 'the power of', 'match made', 'nasdaq', 'nyse', 'tyo', 'hk '];

for (const item of items) {
  let html = item.json.data || (typeof item.json === 'string' ? item.json : JSON.stringify(item.json));
  const companyBlockPattern = /\{[^{}]*"type"\s*:\s*"company"[^{}]*"slug"\s*:\s*"([a-z0-9-]+)"[^{}]*\}/gi;
  let match;
  while ((match = companyBlockPattern.exec(html)) !== null) {
    const slug = match[1].trim();
    if (!slug || slug.length < 2 || slug.length > 50) continue;
    if (slug.includes('acq') || slug === 'portfolio' || slug === 'company') continue;
    let name = slug.split('-').map(w => w.charAt(0).toUpperCase() + w.slice(1)).join(' ');
    const nameLower = name.toLowerCase();
    if (junkPatterns.some(p => nameLower.includes(p))) continue;
    if (/^[A-Z][a-z]+ [A-Z][a-z]+$/.test(name) && !/\d/.test(name)) continue;
    const key = name.toLowerCase().replace(/[^a-z0-9]/g, '');
    if (key.length < 2 || seen.has(key)) continue;
    seen.add(key);
    const contextStart = Math.max(0, match.index - 200);
    const contextEnd = Math.min(html.length, match.index + 500);
    const context = html.substring(contextStart, contextEnd);
    let companyUrl = '';
    const urlMatch = context.match(/"(?:website|url|href)"\s*:\s*"(https?:\/\/[^"]+)"/i);
    if (urlMatch) companyUrl = urlMatch[1];
    let description = name + ' - Headline portfolio company';
    const tagMatch = context.match(/"tag"\s*:\s*"([^"]+)"/i);
    if (tagMatch && !['active', 'exited', 'north america', 'europe', 'asia'].includes(tagMatch[1].toLowerCase())) {
      description = name + ' - Headline portfolio (' + tagMatch[1] + ')';
    }
    results.push({ json: { company_name: name, company_url: companyUrl, description: description, source: vcName } });
  }
}
return results.length ? results : [{ json: { _empty: true } }];

