// Parse Trilogy Equity Partners
const items = $input.all();
const results = [];
const vcName = 'Trilogy Equity Partners';
const seen = new Set();

for (const item of items) {
  let html = item.json.data || (typeof item.json === 'string' ? item.json : JSON.stringify(item.json));
  const modalUrls = {};
  const modalStartPattern = /id="(modal--\d+)"/g;
  let modalMatch;
  while ((modalMatch = modalStartPattern.exec(html)) !== null) {
    const modalId = modalMatch[1];
    const startIdx = modalMatch.index;
    const section = html.substring(startIdx, startIdx + 5000);
    const urlMatch = section.match(/class="website"[^>]*>.*?href="([^"]+)"/i);
    if (urlMatch) modalUrls[modalId] = urlMatch[1];
  }
  const itemPattern = /data-modal="(modal--\d+)"[^>]*>\s*<div class="portfolio--content">\s*<h4>([^<]+)<\/h4>/g;
  let match;
  while ((match = itemPattern.exec(html)) !== null) {
    const modalId = match[1];
    const name = match[2].trim();
    if (!name || name.length < 2 || name.length > 60) continue;
    const key = name.toLowerCase().replace(/[^a-z0-9]/g, '');
    if (key.length < 2 || seen.has(key)) continue;
    seen.add(key);
    const url = modalUrls[modalId] || '';
    results.push({ json: { company_name: name, company_url: url, description: name + ' - Trilogy Equity Partners portfolio company (Seattle enterprise/consumer)', source: vcName } });
  }
}
return results.length ? results : [{ json: { _empty: true } }];

// Combine all items and filter empty results
const allItems = $input.all();
const results = [];
const seen = new Set();

for (const item of allItems) {
  // Skip empty markers
  if (item.json._empty) continue;

  const name = item.json.company_name;
  if (!name) continue;

  // Deduplicate by company name
  const key = name.toLowerCase().replace(/[^a-z0-9]/g, '');
  if (seen.has(key)) continue;
  seen.add(key);

  results.push(item);
}

return results.length ? results : [{ json: { _empty: true, _message: 'No companies found across all VCs' } }];
