      ['Wheel', 'https://www.wheel.com']
    ]
  },
  {
    vc: 'Oak HC/FT',
    companies: [
      ['1upHealth', 'https://www.1uphealth.com'],
      ['Axuall', 'https://www.axuall.com'],
      ['Carrot Fertility', 'https://www.get-carrot.com'],
      ['Cleerly', 'https://www.cleerly.com'],
      ['Grow Therapy', 'https://www.growtherapy.com'],
      ['Ribbon Health', 'https://www.ribbonhealth.com'],
      ['Spring Health', 'https://www.springhealth.com']
    ]
  }
];

for (const { vc, companies } of vcData) {
  for (const [name, url] of companies) {
    const key = name.toLowerCase().replace(/[^a-z0-9]/g, '');
    if (seen.has(key)) continue;
    seen.add(key);
    results.push({
      json: {
        company_name: name,
        company_url: url,
        description: `${name} - ${vc} portfolio company (healthcare)`,
        source: vc
      }
    });
  }
}

return results;
