const allItems = $input.all();
const newCompanies = [];
const existingRecords = [];
for (const item of allItems) {
  if (item.json._isNewCompany) newCompanies.push(item);
  else if (item.json.fields && item.json.fields['Company Name']) existingRecords.push(item);
  else if (item.json.id && item.json['Company Name']) existingRecords.push({ json: { fields: item.json } });
}
const normalize = (str) => str ? String(str).toLowerCase().replace(/[^a-z0-9]/g, '').trim() : '';
const existingNames = new Set();
for (const item of existingRecords) {
  const name = (item.json.fields || item.json)['Company Name'] || '';
  if (name) existingNames.add(normalize(name));
}
const uniqueCompanies = [];
const addedNames = new Set();
for (const company of newCompanies) {
  const name = company.json['Company Name'];
  if (!name) continue;
  const normalizedName = normalize(name);
  if (!normalizedName || existingNames.has(normalizedName) || addedNames.has(normalizedName)) continue;
  addedNames.add(normalizedName);
  const cleanCompany = { ...company.json };
  delete cleanCompany._isNewCompany;
  uniqueCompanies.push({ json: cleanCompany });
}
return uniqueCompanies.length ? uniqueCompanies : [{ json: { _empty: true, _message: 'No new companies found' } }];

// Build evaluation prompt - process ALL items
const companyItems = $('IF: Auto-Disqualify').all().filter(item => !item.json.isAutoDisqualified);
const tidePoolProfile = $('Fetch Tide Pool Profile').first().json;

const systemPrompt = `You are evaluating a company for job search fit based on the Tide Pool Agent Lens criteria.

TIDE POOL PROFILE (Key Criteria):
- Target: VP/Head/Director of Customer Support/Success Operations roles
- Ideal Company: Pre-Series A to Series A, 0-50 employees, VC-backed (not PE)
- Builder Focus: "First hire", "build from scratch", creating/scaling support operations
- Target Sectors: Healthcare, environmental/climate, life sciences, education, audio/music tech, enterprise AI/ML
- Auto-Disqualify: PE-backed, 500+ employees, $200M+ funding, Fortune 500, acquired companies
- Title Flexibility: At <50 person companies, title irrelevant - focus on builder scope
- High-Touch AI: Enterprise AI (governance, compliance, analytics) needs CS from day one

SCORING FRAMEWORK (100 points max):
- Company Stage & Fit: up to 40 pts
  - Pre-Seed/Seed: 40 pts (ideal early builder)
  - Series A: 35 pts (still early)
  - Series B: 20 pts (getting late)
  - Series C+: 5 pts (too late)
- Sector Match: up to 30 pts
  - Target sector (healthcare, climate, life sciences, education, audio/music, enterprise AI): 30 pts
  - Adjacent sector: 15 pts
  - Unrelated sector: 5 pts
- CS Hire Likelihood: up to 30 pts
  - B2B SaaS/Enterprise with high-touch product: 30 pts
  - Mid-market B2B: 20 pts
  - Consumer or self-serve: 10 pts

PENALTIES (subtract from score):
- PE-backed: -50 pts (auto-skip)
- 200+ employees: -20 pts
- Founded before 2018: -15 pts
- Series C or later: -20 pts

Evaluate the company and return this JSON:
{
  "score": <0-100 integer>,
  "sector_match": "target" | "adjacent" | "unrelated",
  "cs_hire_likelihood": "high" | "medium" | "low" | "unlikely",
  "product_type": "high-touch enterprise" | "mid-market" | "self-serve" | "consumer" | "unknown",
  "ops_gap": true | false,
  "summary": "<2-3 sentence explanation>"
}

Respond ONLY with valid JSON, no markdown or explanation.`;

return companyItems.map(item => {
  const companyData = item.json;
  const userPrompt = `COMPANY TO EVALUATE:

Name: ${companyData['Company Name']}
VC Firm: ${companyData['VC Firm']}
URL: ${companyData['Company URL']}
Description: ${companyData['Company Description'] || 'Not available'}

ENRICHMENT DATA:
- Employee Count: ${companyData.enrichment?.employeeCount || 'Unknown'}
- Funding Stage: ${companyData.enrichment?.fundingStage || 'Unknown'}
- Total Funding: ${companyData.enrichment?.totalFundingRaw || 'Unknown'}
- PE-Backed: ${companyData.enrichment?.isPEBacked ? 'Yes (' + companyData.enrichment.peBackerFound + ')' : 'No'}
- VC-Backed: ${companyData.enrichment?.isVCBacked ? 'Yes' : 'Unknown'}
- Founded Year: ${companyData.enrichment?.foundedYear || 'Unknown'}

AUTO-DISQUALIFIERS: ${companyData.autoDisqualifiers?.length > 0 ? companyData.autoDisqualifiers.join('; ') : 'None'}

Provide your evaluation as JSON.`;

  return {
    json: {
      ...companyData,
      evaluationPrompt: { systemPrompt, userPrompt }
    }
  };
});

// Parse Claude's evaluation response - process ALL items
const responseItems = $input.all();
const companyItems = $('Build Evaluation Prompt').all();

return responseItems.map((item, index) => {
  const response = item.json;
  const companyData = companyItems[index]?.json || {};

  let evaluationText = '';
  if (response.content && Array.isArray(response.content)) {
    evaluationText = response.content.filter(c => c.type === 'text').map(c => c.text).join('');
  } else if (typeof response.content === 'string') {
    evaluationText = response.content;
  }

  let evaluation = {};
  try {
    const jsonMatch = evaluationText.match(/\{[\s\S]*\}/);
    if (jsonMatch) evaluation = JSON.parse(jsonMatch[0]);
  } catch (e) {
    evaluation = { score: 0, cs_hire_likelihood: 'unknown', product_type: 'unknown', ops_gap: false, summary: `Parse error: ${e.message}` };
  }

  let status = 'Research';
  const score = evaluation.score || 0;
  if (score >= 70) status = 'Apply';
  else if (score >= 50) status = 'Monitor';
  else if (score >= 30) status = 'Research';
  else status = 'Skip';

  return {
    json: {
      'Company Name': companyData['Company Name'],
      'Company URL': companyData['Company URL'],
      'Company Description': companyData['Company Description'],
      'VC Firm': companyData['VC Firm'],
      'Tide-Pool Score': score,
      'Sector Match': evaluation.sector_match || 'unknown',
      'CS Hire Likelihood': evaluation.cs_hire_likelihood || 'unknown',
      'Product Type': evaluation.product_type || 'unknown',
      'Ops Gap': evaluation.ops_gap || false,
      'Status': status,
      'Summary': evaluation.summary || 'No summary available',
      'Employee Count': companyData.enrichment?.employeeCount || null,
      'Funding Stage': companyData.enrichment?.fundingStage || '',
      'Total Funding': companyData.enrichment?.totalFundingRaw || '',
      'PE Backed': companyData.enrichment?.isPEBacked || false,
      'Founded Year': companyData.enrichment?.foundedYear || null,
      'Disqualify Reasons': companyData.autoDisqualifiers?.length > 0 ? companyData.autoDisqualifiers.join('; ') : ''
    }
  };
});

