// Parse Brave Search results - process ALL items with improved disqualifiers
const braveResponses = $input.all();
const companyDataItems = $('Build Search Query').all();

const peFirms = ['Vista Equity', 'Thoma Bravo', 'KKR', 'Blackstone', 'TPG', 'Carlyle', 'Bain Capital', 'Silver Lake', 'Francisco Partners', 'Vector Capital', 'Ares Management', 'Apollo Global', 'Warburg Pincus', 'Golden Gate Capital', 'Nordic Capital', 'TA Associates', 'Insight Partners', 'General Atlantic', 'Summit Partners'];
const vcFirms = ['a16z', 'Andreessen Horowitz', 'Sequoia', 'Accel', 'Greylock', 'First Round', 'NEA', 'Lightspeed', 'Bessemer', 'Battery Ventures', 'Foundation Capital', 'Index Ventures', 'Benchmark', 'Kleiner Perkins', 'GV', 'Google Ventures', 'Founders Fund', 'Y Combinator', 'YC', 'Accomplice', 'Underscore VC'];
const bigAcquirers = ['Google', 'Alphabet', 'Microsoft', 'Amazon', 'Apple', 'Meta', 'Facebook', 'Oracle', 'Salesforce', 'Adobe', 'SAP', 'IBM', 'Cisco', 'Intel', 'Nest', 'UnitedHealth', 'CVS', 'Optum', 'Teladoc'];

const currentYear = new Date().getFullYear();

return braveResponses.map((item, index) => {
  const braveResponse = item.json;
  const companyData = companyDataItems[index]?.json || {};
  const searchResults = braveResponse?.web?.results || braveResponse?.results || [];
  const allText = searchResults.map(r => `${r.title || ''} ${r.description || ''} ${r.extra_snippets?.join(' ') || ''}`).join(' ');

  let employeeCount = null;
  const empPatterns = [/([\d,]+)\s*(?:to\s*[\d,]+)?\s*employees?/gi, /team\s*(?:of)?\s*([\d,]+)/gi];
  for (const pattern of empPatterns) {
    const matches = [...allText.matchAll(pattern)];
    for (const match of matches) {
      const count = parseInt(match[1].replace(/,/g, ''));
      if (count > 0 && count < 1000000 && (!employeeCount || count > employeeCount)) employeeCount = count;
    }
  }

  const isPublic = /\b(?:IPO|went public|publicly traded|publicly held|stock price|NYSE|NASDAQ|traded on|ticker|stock symbol|\([A-Z]{1,5}\))\b/i.test(allText);
  const acquiredMatch = allText.match(/(?:acquired by|bought by|purchased by|acquisition by)\s+([\w\s]+?)(?:\s+(?:for|in))/i);
  const isAcquired = !!acquiredMatch || bigAcquirers.some(a => new RegExp(`acquired by ${a}|${a} acqui`, 'i').test(allText));
  const acquiredBy = acquiredMatch ? acquiredMatch[1].trim() : (bigAcquirers.find(a => new RegExp(`acquired by ${a}|${a} acqui`, 'i').test(allText)) || null);

  let fundingStage = null;
  if (isPublic) {
    fundingStage = 'Public/IPO';
  } else {
    const stagePatterns = {'Series F+': /\bSeries\s*[F-Z]\b/i, 'Series E': /\bSeries\s*E\b/i, 'Series D': /\bSeries\s*D\b/i, 'Series C': /\bSeries\s*C\b/i, 'Series B': /\bSeries\s*B\b/i, 'Series A': /\bSeries\s*A\b/i, 'Seed': /\b(?:Seed\s*(?:round|funding)?|seed-stage)\b/i, 'Pre-Seed': /\b(?:Pre[- ]?Seed|angel\s*round)\b/i};
    for (const [stage, pattern] of Object.entries(stagePatterns)) {
      if (pattern.test(allText)) { fundingStage = stage; break; }
    }
  }

  let totalFunding = null, totalFundingRaw = null;
  const fundPatterns = [/raised\s*\$([\d.]+)\s*(million|billion|M|B)/gi, /\$([\d.]+)\s*(million|billion|M|B)\s*(?:in\s*)?(?:total\s*)?funding/gi];
  for (const pattern of fundPatterns) {
    const matches = [...allText.matchAll(pattern)];
    for (const match of matches) {
      const amount = parseFloat(match[1]);
      const unit = match[2].toLowerCase();
      let value = (unit === 'billion' || unit === 'b') ? amount * 1000000000 : amount * 1000000;
      if (!totalFunding || value > totalFunding) { totalFunding = value; totalFundingRaw = `$${amount}${unit.charAt(0).toUpperCase()}`; }
    }
  }

  const isPEBacked = peFirms.some(f => new RegExp(f.replace(/\s+/g, '\\s*'), 'i').test(allText));
  const peBackerFound = peFirms.find(f => new RegExp(f.replace(/\s+/g, '\\s*'), 'i').test(allText)) || null;
  const isVCBacked = vcFirms.some(f => new RegExp(f.replace(/\s+/g, '\\s*'), 'i').test(allText));

  let foundedYear = null;
  const foundMatch = allText.match(/(?:founded|established|since|started)\s*(?:in)?\s*(\d{4})/i);
  if (foundMatch) { const year = parseInt(foundMatch[1]); if (year >= 1990 && year <= currentYear) foundedYear = year; }

  const autoDisqualifiers = [];
  if (isPEBacked) autoDisqualifiers.push(`PE-backed (${peBackerFound})`);
  if (employeeCount >= 500) autoDisqualifiers.push(`500+ employees (${employeeCount})`);
  if (totalFunding >= 200000000) autoDisqualifiers.push(`$200M+ funding (${totalFundingRaw})`);
  if (isPublic || fundingStage === 'Public/IPO') autoDisqualifiers.push('Public company');
  if (isAcquired) autoDisqualifiers.push(`Acquired${acquiredBy ? ' by ' + acquiredBy : ''}`);
  if (foundedYear && foundedYear < 2017) autoDisqualifiers.push(`Founded ${foundedYear} (${currentYear - foundedYear}+ years old)`);
  if (['Series D', 'Series E', 'Series F+'].includes(fundingStage)) autoDisqualifiers.push(`Late stage (${fundingStage})`);

  return {
    json: {
      'Company Name': companyData['Company Name'],
      'Company URL': companyData['Company URL'],
      'Company Description': companyData['Company Description'],
      'VC Firm': companyData['VC Firm'],
      'Status': companyData['Status'],
      'First Seen Date': companyData['First Seen Date'],
      'Source': companyData['Source'],
      enrichment: { employeeCount, fundingStage, totalFunding, totalFundingRaw, isPEBacked, peBackerFound, isVCBacked, foundedYear, isPublic, isAcquired, acquiredBy, searchResultCount: searchResults.length },
      autoDisqualifiers,
      isAutoDisqualified: autoDisqualifiers.length > 0,
      needsManualResearch: searchResults.length === 0,
      hasEnrichmentData: !!(employeeCount || fundingStage || totalFunding || isPEBacked || isVCBacked || isPublic || isAcquired)
    }
  };
});
