const results = [];
const seen = new Set();

const vcData = [
  {
    vc: 'Backstage Capital',
    companies: [
      ['The Lip Bar', 'https://www.thelipbar.com'],
      ['Pigeonly', 'https://www.pigeonly.com'],
      ['Mayvenn', 'https://www.mayvenn.com'],
      ['Partake Foods', 'https://www.partakefoods.com'],
      ['Blavity', 'https://www.blavity.com'],
      ['Health In Her HUE', 'https://www.healthinherhue.com'],
      ['PlayVS', 'https://www.playvs.com']
    ]
  },
  {
    vc: 'Harlem Capital',
    companies: [
      ['HBCU 20x20', 'https://www.hbcu20x20.com'],
      ['Kanarys', 'https://www.kanarys.com'],
      ['Sana Benefits', 'https://www.sanabenefits.com'],
      ['Stackwell', 'https://www.getstackwell.com'],
      ['Vori Health', 'https://www.vorihealth.com']
    ]
  },
  {
    vc: 'Collab Capital',
    companies: [
      ['Goodr', 'https://www.goodr.co'],
      ['Autobon.ai', 'https://www.autobon.ai'],
      ['Caribu', 'https://www.caribu.com'],
      ['SkillSmart', 'https://www.skillsmart.io'],
      ['Pangea', 'https://www.pangeamoney.com']
    ]
  }
];

for (const { vc, companies } of vcData) {
  for (const [name, url] of companies) {
    const key = name.toLowerCase().replace(/[^a-z0-9]/g, '');
    if (seen.has(key)) continue;
    seen.add(key);
    results.push({
      json: {
        company_name: name,
        company_url: url,
        description: `${name} - ${vc} portfolio company (social impact)`,
        source: vc
      }
    });
  }
}

return results;
