// Parse Backstage Capital - underrepresented founders
// Company links point to /headliners/company-slug/
const items = $input.all();
const results = [];
const vcName = 'Backstage Capital';
const seen = new Set();
const today = new Date().toISOString().split('T')[0];

for (const item of items) {
  let html = item.json.data || (typeof item.json === 'string' ? item.json : JSON.stringify(item.json));

  // Primary pattern: links to /headliners/company-slug/ with <strong>Name</strong> inside
  // Example: <a href="/headliners/company-name/"><strong>Company Name</strong>...</a>
  const headlinerLinkPattern = /<a[^>]*href=["']\/headliners\/([a-z0-9-]+)\/?["'][^>]*>[\s\S]*?<strong>([^<]+)<\/strong>/gi;

  let match;
  while ((match = headlinerLinkPattern.exec(html)) !== null) {
    const slug = match[1].trim();
    const name = match[2].trim();

    if (!name || name.length < 2 || name.length > 60) continue;

    const key = name.toLowerCase().replace(/[^a-z0-9]/g, '');
    if (key.length < 2 || seen.has(key)) continue;
    seen.add(key);

    results.push({ json: { 'Company Name': name, 'VC Firm': vcName, 'Company Description': `${name} - Backstage Capital portfolio company (underrepresented founders)`, 'Company URL': '', 'Status': 'New', 'First Seen Date': today, 'Portfolio Addition Date': today, 'Source': 'VC Portfolio' } });
  }

  // Fallback: just look for /headliners/slug/ links and convert slug to name
  if (results.length === 0) {
    const slugPattern = /href=["']\/headliners\/([a-z0-9-]+)\/?["']/gi;
    while ((match = slugPattern.exec(html)) !== null) {
      const slug = match[1].trim();
      if (!slug || slug.length < 2) continue;

      // Convert slug to name: 'company-name' -> 'Company Name'
      const name = slug.split('-').map(w => w.charAt(0).toUpperCase() + w.slice(1)).join(' ');

      const key = slug.replace(/[^a-z0-9]/g, '');
      if (seen.has(key)) continue;
      seen.add(key);

      results.push({ json: { 'Company Name': name, 'VC Firm': vcName, 'Company Description': `${name} - Backstage Capital portfolio company (underrepresented founders)`, 'Company URL': '', 'Status': 'New', 'First Seen Date': today, 'Portfolio Addition Date': today, 'Source': 'VC Portfolio' } });
    }
  }
}
return results.length ? results : [{ json: { _empty: true, _message: 'No companies found', vcName } }];

