    // No external URL available from this page
    results.push({ json: { 'Company Name': name, 'VC Firm': vcName, 'Company Description': `${name} - Collab Capital portfolio company (Black entrepreneurs)`, 'Company URL': '', 'Status': 'New', 'First Seen Date': today, 'Portfolio Addition Date': today, 'Source': 'VC Portfolio' } });
