}
return results.length ? results : [{ json: { _empty: true, _message: 'No companies found', vcName } }];

// Parse Cleo Capital - diverse founders (Sarah Kunst)
const items = $input.all();
const results = [];
const vcName = 'Cleo Capital';
const seen = new Set();
const today = new Date().toISOString().split('T')[0];

const skipPatterns = ['cleo', 'capital', 'portfolio', 'about', 'team', 'contact', 'linkedin', 'twitter', 'sarah', 'kunst', 'consumer', 'enterprise', 'fintech'];

for (const item of items) {
  let html = item.json.data || (typeof item.json === 'string' ? item.json : JSON.stringify(item.json));

  // Squarespace-based site
  const patterns = [
    /<h[2-5][^>]*>([A-Z][A-Za-z0-9\s\-&\.]+)<\/h[2-5]>/gi,
    /<img[^>]*alt=["']([A-Z][A-Za-z0-9\s\-&\.]+)["'][^>]*>/gi,
    /<a[^>]*href=["'](https?:\/\/(?!cleocap|squarespace|linkedin|twitter)[^"']+)["'][^>]*>/gi
  ];

  // First pass: extract from alt tags and headings
  for (let i = 0; i < 2; i++) {
    const pattern = patterns[i];
    let match;
    while ((match = pattern.exec(html)) !== null) {
      let name = match[1].trim();

      if (!name || name.length < 2 || name.length > 60) continue;
      if (skipPatterns.some(p => name.toLowerCase().includes(p))) continue;
      if (/^(View|Load|See|All|More|Portfolio|Logo|Image)$/i.test(name)) continue;
      if (/logo/i.test(name)) continue;

      const key = name.toLowerCase().replace(/[^a-z0-9]/g, '');
      if (key.length < 2 || seen.has(key)) continue;
      seen.add(key);

      results.push({ json: { 'Company Name': name, 'VC Firm': vcName, 'Company Description': `${name} - Cleo Capital portfolio company (diverse founders)`, 'Company URL': '', 'Status': 'New', 'First Seen Date': today, 'Portfolio Addition Date': today, 'Source': 'VC Portfolio' } });
    }
  }

  // Second pass: extract URLs and derive company names
  if (results.length < 5) {
    const urlPattern = patterns[2];
    let match;
    while ((match = urlPattern.exec(html)) !== null) {
      const url = match[1].trim();
      if (!url || url.includes('#') || url.includes('?')) continue;

      const domainMatch = url.match(/https?:\/\/(?:www\.)?([^.\/]+)/i);
      if (!domainMatch) continue;

      let name = domainMatch[1];
      name = name.charAt(0).toUpperCase() + name.slice(1);

      const key = name.toLowerCase().replace(/[^a-z0-9]/g, '');
      if (key.length < 2 || seen.has(key)) continue;
      seen.add(key);

      results.push({ json: { 'Company Name': name, 'VC Firm': vcName, 'Company Description': `${name} - Cleo Capital portfolio company`, 'Company URL': url, 'Status': 'New', 'First Seen Date': today, 'Portfolio Addition Date': today, 'Source': 'VC Portfolio' } });
    }
  }
}
return results.length ? results : [{ json: { _empty: true, _message: 'No companies found', vcName } }];

